package bit701.day0901;

public class Ex4_For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 초기값이 1인데 조건이0보다 작을 경우에만 반복하므로 한번도 실행을 안함
		for(int a=1;a<0;a++) {
			System.out.println("a="+a);
			
		}

			System.out.println("=".repeat(30));//"="을 30 번 반복
			for(int a=1;a<=10;a++){
			    System.out.printf("%3d",a);
			}
			System.out.println();
			System.out.println("=".repeat(30));//"="을 30 번 반복

			for(int a=1;a<=20;a+=3){
			    System.out.printf("%3d",a);
			}
			System.out.println();
			System.out.println("=".repeat(30));//"="을 30 번 반복

			for(int a=1;a<=10;a++){
			    System.out.printf("%3d",a);
			}
			System.out.println();
			System.out.println("=".repeat(30));//"="을 30 번 반복

			for(int a=1;a<=10;a++){
			    System.out.printf("%3d",a);
			}
			System.out.println();
			System.out.println("=".repeat(30));//"="을 30 번 반복		
		
		
	}

}
